export default {
  name: "Tr2",
  data() {
    return {
      scene: new THREE.Scene(),
      //创建场景
      camera: new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000),
      //创建相机
      renderer: new THREE.WebGLRenderer({
        alpha: true
      }) //创建渲染器
    };
  },

  methods: {
    createBox() {
      const gl = new THREE.BoxGeometry(1.3, 0.2, 1); // 定义几何体类型
      const ml = new THREE.LineBasicMaterial({
        transparent: true,
        opacity: 0.8,
        color: "#2C425E"
      }); //定义线条材质

      // 给出立方体
      this.cube = new THREE.Mesh(gl, ml);
      this.scene.add(this.cube);

      // 添加边框
      const edges = new THREE.EdgesGeometry(gl);
      const ll = new THREE.LineBasicMaterial({
        color: '#36ABA7',
        linewidth: 2
      }); // black color for the edges
      const ls = new THREE.LineSegments(edges, ll);
      this.scene.add(ls);
    },
    createLine() {
      const points = [];
      points.push(new THREE.Vector3(0, 0, 0));
      points.push(new THREE.Vector3(0, 2, 0));
      points.push(new THREE.Vector3(1, 2, 0));
      const gl = new THREE.BufferGeometry().setFromPoints(points); // 定义几何体类型
      const ml = new THREE.LineBasicMaterial({
        color: 0x0000ff
      }); //定义线条材质

      // 给出线条
      this.line = new THREE.Line(gl, ml);
      this.scene.add(this.line);
    },
    animate() {
      requestAnimationFrame(this.animate);
      // this.controls.update(); // 启动阻尼时
      // 设置动画
      if (this.cube.rotation.x <= 1 && this.isl == 1) {
        this.cube.rotation.x += 0.001;
        this.cube.rotation.y += 0.001;
      } else if (isl != 2) {
        this.isl = 2;
      }
      if (this.cube.rotation.x > 0 && this.isl == 2) {
        this.cube.rotation.x -= 0.001;
        this.cube.rotation.y -= 0.001;
      } else {
        this.isl = 1;
      }

      // 渲染器对场景和摄像头进行渲染
      this.renderer.render(this.scene, this.camera);
    }
  },
  mounted() {
    // this.renderer.setSize(window.innerWidth,window.innerHeight);
    this.renderer.setSize(200, 100);
    this.camera.position.set(0, 1, 0);
    this.camera.lookAt(0, 0, 0);
    this.$refs.threecontent.appendChild(this.renderer.domElement);
    this.createBox();
    this.isl = 1;
    this.cube.rotation.x = 0;
    this.cube.rotation.y = 0;

    // 实现循环动画
    this.animate();
  }
};
