export default {
  name: "Tr2",
  data() {
    return {
      scene: new THREE.Scene(),
      //创建场景
      camera: new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000),
      //创建相机
      renderer: new THREE.WebGLRenderer({
        alpha: true
      }) //创建渲染器
    };
  },

  methods: {
    createBox() {
      const gl = new THREE.BoxGeometry(20, 1, 1); // 定义几何体类型
      const ml = new THREE.LineBasicMaterial({
        color: 0x0000ff
      }); //定义线条材质

      // 给出立方体
      this.cube = new THREE.Mesh(gl, ml);
      this.scene.add(this.cube);
    },
    createLine() {
      const points = [];
      points.push(new THREE.Vector3(0, 0, 0));
      points.push(new THREE.Vector3(0, 2, 0));
      points.push(new THREE.Vector3(1, 2, 0));
      const gl = new THREE.BufferGeometry().setFromPoints(points); // 定义几何体类型
      const ml = new THREE.LineBasicMaterial({
        color: 0x0000ff
      }); //定义线条材质

      // 给出线条
      this.line = new THREE.Line(gl, ml);
      this.scene.add(this.line);
    },
    animate() {
      requestAnimationFrame(this.animate);
      // this.controls.update(); // 启动阻尼时

      // 渲染器对场景和摄像头进行渲染
      this.renderer.render(this.scene, this.camera);
    }
  },
  mounted() {
    // this.renderer.setSize(window.innerWidth,window.innerHeight);
    this.renderer.setSize(500, 500);
    this.camera.position.set(0, 0, 1);
    this.$refs.threecontent.appendChild(this.renderer.domElement);
    this.createBox();
    // 实现循环动画
    this.animate();
  }
};
