import VueRouter from 'vue-router'
import Vue from 'vue'

import Home from '../components/Home'
import Test from '../components/Test'
import Create from '../components/Create'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'Home',
    component:Home,
    meta:{requestLogin:false},
  },
  {
    path: '/test',
    name: 'Test',
    component:Test,
    meta:{requestLogin:false},
  },
  {
    path: '/create',
    name: 'Create',
    component:Create,
    meta:{requestLogin:true},
  },
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

router.beforeEach((to,from,next)=>{
  if(to.name==='Home'){
    console.log('欢迎回家，但是需要...')
    localStorage.setItem('token','')
  }
  if(!to.meta.requestLogin||localStorage.getItem('token')){
    next()
  }else{
    next('/test')
  }
})

export default router
