import VueRouter from 'vue-router'
import Vue from 'vue'

Vue.use(VueRouter)

const routes = [
  
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

router.beforeEach((to,from,next)=>{
  if(to.name==='Home'){
    console.log('欢迎回家，但是需要...')
    localStorage.setItem('token','')
  }else{
    console.log('11')
  }
  if(!to.meta.requestLogin||localStorage.getItem('token')){
    next()
  }else{
    next('/test')
  }
})

export default router
